mapper.select:
SELECT pltID,pltName,pltNumCode,pltProductivity
FROM corenets_productline AS A
;

SELECT pltID,pltName,pltNumCode,pltProductivity
FROM corenets_productline AS A
;

SELECT pltID,pltName,pltNumCode,pltProductivity
FROM corenets_productline AS A
WHERE 1=1 AND A.pltID=#{pId}
;

SET @tmpId_new=0;
SET @tmpNumCode=CONCAT(#{pNumCodeHead},DATE_FORMAT(NOW(),"%Y%m%d%h%i%s"),FLOOR(RAND()*100000));

INSERT INTO corenets_productline(pltNumCode,pltCreateTime,pltModifyTime
,pltID
,pltName
,pltNumCode
,pltProductivity
,pltCreateBy
,pltCreateTime
,pltModifyBy
,pltModifyTime

)VALUE(@tmpNumCode,now(),now()
,#{pPojo.pltID}
,#{pPojo.pltName}
,#{pPojo.pltNumCode}
,#{pPojo.pltProductivity}
,#{pPojo.pltCreateBy}
,#{pPojo.pltCreateTime}
,#{pPojo.pltModifyBy}
,#{pPojo.pltModifyTime}

);

SELECT LAST_INSERT_ID();

mapper.update:
UPDATE corenets_productline AS A SET pltModifyTime=now()
,pltID=#{pPojo.pltID}
,pltName=#{pPojo.pltName}
,pltNumCode=#{pPojo.pltNumCode}
,pltProductivity=#{pPojo.pltProductivity}
,pltCreateBy=#{pPojo.pltCreateBy}
,pltCreateTime=#{pPojo.pltCreateTime}
,pltModifyBy=#{pPojo.pltModifyBy}
,pltModifyTime=#{pPojo.pltModifyTime}

WHERE 1=1 AND A.pltID=#{pId};

mapper.select:
SELECT pltID,pltName,pltNumCode,pltProductivity,"" AS LastEndTime,"0" AS ProductCount,"0" AS ProductNeedDay
