mapper.select:
SELECT shstID,shstName,shstNumCode,shstPrice,shstCost,shstTimeStandard
FROM corenets_product AS A
WHERE 1=1 AND A.shstID=#{pId}
;

SET @tmpId_new=0;
SET @tmpNumCode=CONCAT(#{pNumCodeHead},DATE_FORMAT(NOW(),"%Y%m%d%h%i%s"),FLOOR(RAND()*100000));

INSERT INTO corenets_product(shstNumCode,shstCreateTime,shstModifyTime
,shstID
,shstName
,shstNumCode
,shstPrice
,shstCost
,shstTimeStandard
,shstCreateBy
,shstCreateTime
,shstModifyBy
,shstModifyTime

)VALUE(@tmpNumCode,now(),now()
,#{pPojo.shstID}
,#{pPojo.shstName}
,#{pPojo.shstNumCode}
,#{pPojo.shstPrice}
,#{pPojo.shstCost}
,#{pPojo.shstTimeStandard}
,#{pPojo.shstCreateBy}
,#{pPojo.shstCreateTime}
,#{pPojo.shstModifyBy}
,#{pPojo.shstModifyTime}

);

SELECT LAST_INSERT_ID();

mapper.update:
UPDATE corenets_product AS A SET shstModifyTime=now()
,shstID=#{pPojo.shstID}
,shstName=#{pPojo.shstName}
,shstNumCode=#{pPojo.shstNumCode}
,shstPrice=#{pPojo.shstPrice}
,shstCost=#{pPojo.shstCost}
,shstTimeStandard=#{pPojo.shstTimeStandard}
,shstCreateBy=#{pPojo.shstCreateBy}
,shstCreateTime=#{pPojo.shstCreateTime}
,shstModifyBy=#{pPojo.shstModifyBy}
,shstModifyTime=#{pPojo.shstModifyTime}

WHERE 1=1 AND A.shstID=#{pId};

mapper.select:
SELECT shstID,shstName,shstNumCode,shstPrice,shstCost,shstTimeStandard,"0" AS whstCount,"0" AS Count,"0" AS ProductCount,"0" AS ProductNeedDay
FROM corenets_product AS A
;
